import React from 'react'

const AddToWatch = ({dispatchWatch,watchItem,oneItem }) => {
    return (
        <>
            <button
                className="btn"
                onClick={() =>
                  dispatchWatch({ type: "WATCH_ITEM", payload: oneItem })
                }
              >{watchItem.some(el=> el._id === oneItem._id) ? 'Watched' : 'add watch' }
                </button>
       </>
    )
}

export default AddToWatch
