import { React, useContext, useEffect,useState } from "react";
import { prodactsConext } from "../context/contextData";
import Cards from "./Cards";
import { getsReq } from "../api/rest-helper";
import { Link,Redirect} from "react-router-dom";

function HomeApi() {
  const { data, dispatch, user, dispatchUser } = useContext(prodactsConext);
  const [redirect, setRedirect] = useState(false);
  console.log("user", user);
  useEffect(() => {

    getsReq().then((result) => {
      dispatch({ type: "FATCH_SUCCESS", payload: result.data });
    }).catch(error => {
      console.log(error.response.status)
        if (error.response.status === 404) {
          setRedirect(true);
    }
    })
  }, []);

  return (

    <div className="container">
       {redirect ? <Redirect to="/login" /> : ""}
      <div className="row">
        <Link to="/sellerForm">Sell Somthing</Link>
        <Link to="/watchList">Watched List</Link>
        <Link to="/basketList">Basket List</Link>
        <Link to="/register">Register</Link>
        {user.user ? (
          <div
            style={{ cursor: "pointer" }}
            onClick={() => {
              dispatchUser({ type: "LOGOUT_USER", payload: "" })
              let keysToRemove = ["user", "x-auth"];
              for (let key of keysToRemove) {
                localStorage.removeItem(key);
              }
            }
          }
          >
            logout
          </div>
        ) : (
          <Link to="/login">Login</Link>
        )}
        {user.user ? (
          <div className="text-primary">
            Hi!.. {user.user.firstName} {user.user.lastName}
          </div>
        ) : (
          ""
        )}
        <h1 className="text-center">Online shop</h1>
        {data.map((event) => {
          return (
            <Cards
              key={event._id}
              card={event}
              data={data}
              setData={dispatch}
            />
          );
        })}
      </div>
    </div>
  );
}

export default HomeApi;
