import { React, useState, useEffect, useContext } from "react";
import Edit from "./Edit";
import Buy from "../basket/Buy";
import { getReq } from "../api/rest-helper";
import { useParams } from "react-router-dom";
import { prodactsConext } from "../context/contextData";
import CheckLogIn from "../CheckLogIn";
import Image from "./Image";
import Tabs from "./Tabs";
import { MotionPathPlugin } from "gsap/MotionPathPlugin";
import { gsap } from "gsap";

gsap.registerPlugin(MotionPathPlugin);

const Item = () => {
  const { id } = useParams();
  const [edit, setEdit] = useState(false);
  const {
    dispatch1,
    user,
    dispatchOneItem,
    oneItem,
    dispatchWatch,
    watchItem,
  } = useContext(prodactsConext);

  const [opened, setOpened] = useState(0);

  const [inPlace, setInPlace] = useState(0);
  const [disabled, setDisabled] = useState(false);

  const onClick = (index) => {
    if (!disabled) setOpened(index);
  };
  const onInPlace = (index) => setInPlace(index);

  const next = () => {
    let nextIndex = opened + 1;
    if (nextIndex >= oneItem.img.length) nextIndex = 0;
    onClick(nextIndex);
  };

  useEffect(() => setDisabled(true), [opened]);
  useEffect(() => setDisabled(false), [inPlace]);
  // React.useEffect(() => {
  // 	if(CodePen && CodePen.isThumbnail)
  // 	{
  // 		setTimeout(() => next(), 100)
  // 	}
  // }, [])

  /*
  {oneItem.img == undefined
    ? ""
    : oneItem.img.map((e) => (
        <img
          style={{ maxWidth: "100%" }}
          src={"http://localhost:3001/" + e}
          alt={oneItem.name}
        />
    ))}
*/
  useEffect(() => {
    getReq(id).then((result) => {
      dispatchOneItem({ type: "FATCH_SUCCESS", payload: result.data });
    });
  }, [id, edit]);

  console.log("item", oneItem);

  return (
    <div className="container">
      {oneItem.img == undefined
                    ? ""
                    : oneItem.img.map((image, i) => (
                        <div
                          key={image.url}
                          className="image"
                          style={{
                            zIndex: inPlace === i ? i : oneItem.img.length + 1,
                          }}
                        >
                          <Image
                            total={oneItem.img.length}
                            id={i}
                            url={"http://localhost:3001/" + image}
                            title={oneItem.title}
                            open={opened === i}
                            inPlace={inPlace === i}
                            onInPlace={onInPlace}
                          />
                        </div>
                      ))}
                  <div className="tabs">
                    <Tabs
                      className="tabs"
                      images={oneItem.img}
                      onSelect={onClick}
                    />
                  </div>


                <button className="button next shadow" onClick={next}>
                  <svg width="24" height="24" viewBox="0 0 24 24">
                    {" "}
                    <path d="M4,11V13H16L10.5,18.5L11.92,19.92L19.84,12L11.92,4.08L10.5,5.5L16,11H4Z" />{" "}
                  </svg>
                </button>
      <div className="row">

        {edit ? (
          <CheckLogIn
            component={() => (
              <Edit
                key={oneItem._id}
                setEdit={setEdit}
                dispatchOneItem={dispatchOneItem}
              />
            )}
          />
        ) : (
          <div className="col-md-4 col-sm-6">
            <h1>Item </h1>
            <div
              className="card"
              style={{ borderColor: oneItem.website ? "" : "red" }}
            >
              <div className="card-header">


              </div>
              <div className="card-body">
                <h2>{oneItem.title}</h2>
                <h5>{oneItem.category}</h5>
                <div>{oneItem.price}</div>
                <div>{oneItem.quantity}</div>
              </div>
              {user.loggedIn &&
              user.user &&
              oneItem.owner &&
              oneItem.owner._id == user.user._id ? (
                <button
                  onClick={() => setEdit(!edit)}
                  className="btn btn-secondary"
                >
                  {" "}
                  update
                </button>
              ) : (
                <Buy key={oneItem._id} card={[oneItem]} />
              )}

              <button
                className="btn"
                onClick={() =>
                  dispatchWatch({ type: "WATCH_ITEM", payload: oneItem })
                }
              >
                {watchItem.some((el) => el._id === oneItem._id)
                  ? "Watched"
                  : "add watch"}
              </button>
              <button
                className="btn"
                onClick={() =>
                  dispatch1({ type: "ADD_ITEM", payload: oneItem })
                }
              >
                add to cart
              </button>
              <button onClick={() => dispatchOneItem({ type: "DECROMENT" })}>
                {" "}
                -{" "}
              </button>
              <button onClick={() => dispatchOneItem({ type: "INCROMENT" })}>
                {" "}
                +{" "}
              </button>
            </div>
          </div>
        )}
      </div>
    </div>
  );
};

export default Item;
