  return (
    <Tilt >
          <div class="card card3D">
              <div class="thumb" style={{ "backgroundImage": `url(${img})` }}>
              <h2>Virtual Reality</h2>
        <span>Dreaming with your eyes open!</span>
        </div>
          </div>
    </Tilt>
  );
}

export default Card3D;
