import React,{useState, useEffect} from 'react'
import { useTransition, animated, useSpring,config } from 'react-spring'

const SliderScreen = ({ el, i }) => {
  const NUM_TRANS = [1,2,3,4]
  const [items, setItems] = useState(NUM_TRANS)

  const transitions = useTransition(items, {
    from: { opacity: 0 },
    enter: { opacity: 1 },
    leave: { opacity: 0 },
    delay: 200,
    config: config.molasses,
    onRest: () => setItems([]),
  })

  useEffect(() => {
    if (items.length === 0) {
      setTimeout(() => {
        setItems(NUM_TRANS)
      }, 2000)
    }
  }, [items])
    return (
      <div className="textCardS">
        {transitions(({ opacity }, item) => (
        <animated.div
          style={{
            opacity: opacity.to(item.op),
            transform: opacity
              .to(item.trans)
              .to(y => `translate3d(0,${y}px,0)`),
          }}>
          {item.fig}
        </animated.div>
      ))}
        <animated.div className={`slideHeader${i} slideHeader`} >
          <h3>{el.title}</h3>
          <p>{el.par}</p>
        </animated.div>
        <img width="100%" src={el.img} alt=""/>
      </div>
    )
}

export default SliderScreen
