import React, { useState, useEffect } from "react";
import axios from "axios";

const SellerForm = () => {
  const [inputText, setInputText] = useState({
    title: "",
    position: "",
    price: "",
  });

  const [addProduct, setAddProduct] = useState([]);
	const [Message	, setMessage] = useState([]);
	const [isData, setisData] = useState(false)
  useEffect(() => {
    sendData();
  }, [addProduct]);


  const handleChange = (e) => {
    const value = e.target.value;
	  setInputText({ ...inputText, [e.target.name]: value });
    setisData(true)
  };

  console.log("addProduct", addProduct);

  const handleSubmit = (e) => {
    e.preventDefault();
    if (isData === false) {
      setMessage({ text: 'add a prodact' })
    } else {
      setAddProduct(inputText);
      setInputText({
        title: "",
        position: "",
        price: "",
      })
      setMessage({ text: 'The data has been sent successfully' })
    }
	  setTimeout(() => {
      setMessage([])
    }, 2000);
  };


	const sendData = () => {

			const myURL = "http://localhost:3001/products";
			axios({
				method: "post",
				url: myURL,
				data: JSON.stringify(addProduct),
				headers: { "Content-Type": "application/json" },
			})
				.then(function (response) {
					//handle success
					console.log(response);

				})
				.catch(function (response) {
					//handle error
					console.log(response);
				});
	}


  return (
    <div>
      <form id="loginForm" method="POST" action="/#">
        <legend>
          <fieldset>Seller Form</fieldset>
          <div className="formBox">
            <label for="name">Name</label>
					  <input
						 value={inputText.title} 
              className="email"
              type="text"
              autocomplete="off"
              name="title"
              onChange={handleChange}
            />
          </div>
          <div className="formBox">
            <label for="position">Position</label>
					  <input
						  value={inputText.position}
              class="pass"
              type="text"
              autocomplete="off"
              name="position"
              onChange={handleChange}
            />
          </div>
          <div class="formBox">
            <div className="formBox">
              <label for="price">Price</label>
						  <input
							  value={inputText.price}
                class="pass"
                type="text"
                autocomplete="off"
                name="price"
                onChange={handleChange}
              />
            </div>
            <div class="formBox"></div>
            <button onClick={handleSubmit}>Submit</button>
          </div>
        </legend>
		  </form>
		  {Message.text}
    </div>
  );
};

export default SellerForm;
