import { React, useContext, useEffect } from "react";
import {prodactsConext} from '../context/contextData'
import Cards from "./Cards";
import { getsReq } from "../api/rest-helper";
import { Link } from "react-router-dom";

function HomeApi() {
  const {data, dispatch, user, dispatchUser} = useContext(prodactsConext);
console.log('user',user)
  useEffect(() => {
    getsReq().then((result) => {
      dispatch({ type: "FATCH_SUCCESS", payload: result.data })
    });
  }, []);

  return (
    <div className="container">
      <div className="row">
        <Link to="/sellerForm">Sell Somthing</Link>
        <Link to="/watchList">Watched List</Link>
        <Link to="/basketList">Basket List</Link>
        <Link to="/register">Register</Link>
        {user.user ? <div style={{cursor: 'pointer'}} onClick={()=> dispatchUser({type:'LOGOUT_USER', payload:''})}>logout</div> : <Link to="/login">Login</Link>}
        {user.user ? <div className='text-success'>HI! {user.user.firstName} {user.user.lastName}</div> :''}
        <h1 className="text-center">Online shop</h1>
        {data.map((event) => {
          return (
            <Cards
              key={event._id}
              card={event}
              data={data}
              setData={dispatch}
            />
          );
        })}
      </div>
    </div>
  );
}

export default HomeApi;
