import axios from "axios";
import { API_ROOT } from "./service-info";



export const putReq = (body, id) => {
    return axios.put(`${API_ROOT}/${id}`, body).then((result) => result.data);
};

export const postReq = async (body) => {
    const respons = await axios.post(`${API_ROOT}/`, body)
      return respons.data
};

export const getReq = (id) => {
 return axios.get(`${API_ROOT}/${id}`).then(result => result.data);
};

export const deleteReq = (id) => {
   return axios.delete(`${API_ROOT}/${id}`).then(result => result.data )
}

export const getsReq = () => {
    return axios.get(`${API_ROOT}/`).then(result => result.data);
};
