import React from 'react'




const WatchList = () => {


    const [gitOneData, setGitOneData] = useState(false);
    const [oneItem, setOneItem] = useState([])

    const {id} = useParams()

     if ( !gitOneData ){
         setGitOneData(true)
        axios.get(`http://localhost:3001/products/${id}`)
         .then( result => setOneItem(result.data) );
       }



    return (
        <div>

<div key={oneItem._id} className="col-md-4 col-sm-6">
 <div className="card" style={{ borderColor: oneItem.website ? "" : "red" }}>
       <div className="card-header">
       {oneItem.img == undefined ? '' : oneItem.img.map(e=> <img style={{ maxWidth: "100%" }} src={'http://localhost:3001/'+e} alt={oneItem.name} />)}

   </div>
   <div className="card-body">
    <h2 >{oneItem.title}</h2>
     <h5>{oneItem.type}</h5>
     <div>{oneItem.price}</div>
     <div></div>
     <div></div>
       </div>
       <button
       onClick={() => setEdit(true)}
       className='btn btn-secondary'> update</button>

       <AddToBasket key={oneItem._id} card={oneItem} />
       <AddToWatchList key={oneItem._id} data={oneItem} setData={setOneItem}/>
 </div>
 </div>
   </div>
    )
}

export default WatchList
