    const { id } = useParams();
    const [edit, setEdit] = useState(false);
    const [oneItem  , setOneItem] = useState([])



useEffect(() => {

        axios.get(`http://localhost:3001/products/${id}`)
        .then( result => setOneItem(result.data) );
}, [id])


