            {oneItem.img == undefined ? '' : oneItem.img.map(e=> <img style={{ maxWidth: "100%" }} src={'http://localhost:3001/'+e} alt={oneItem.name} />)}

        </div>
        <div className="card-body">
         <h2 >{oneItem.title}</h2>
          <h5>{oneItem.type}</h5>
          <div>{oneItem.price}</div>
