import React, { useContext } from "react";
import { Link } from "react-router-dom";
import { prodactsConext } from "../context/contextData";

const NavBar = () => {
  const { data, dispatch, user, dispatchUser } = useContext(prodactsConext);
  return (
    <>



    <header class="header">
		<h1 class="logo"><a href="#">Flexbox</a></h1>
      <ul class="main-nav">
          <li><a href="#">Home</a></li>
          <li><a href="#">About</a></li>
          <li><a href="#">Portfolio</a></li>
          <li><a href="#">Contact</a></li>
          {user.user ? (
          <div
            style={{ cursor: "pointer" }}
            onClick={() => {
              dispatchUser({ type: "LOGOUT_USER", payload: '' })
              let keysToRemove = ["user", "x-auth"];
              for (let key of keysToRemove) {
                localStorage.removeItem(key);
              }
            }
          }
          >
            logout
          </div>
        ) : (
          <Link to="/login">Login</Link>
        )}
        {user.user ? (
          <li className="text-primary">
           <p> Hi!.. {user.user.firstName} {user.user.lastName}</p>
          </li>
        ) : (
          ""
        )}
      </ul>







	</header> 

</>

  )
};

export default NavBar;
