import {React, useRef, useState, useEffect} from "react";


const Cards = ({ card, data, setData }) => {
  const [edit, setEdit] = useState({
    id: null,
    title: '',
    position: '',
    price: ''
  });
  const [input, setInput] = useState(edit ?{
    title: edit.value,
    position: edit.value,
    price: edit.value
  }:'');
  // const [input, setInput] = useState(edit ? edit.value : '');
 // const inputRef = useRef(null);

  useEffect(() => {
   // inputRef.current.focus();
  });

console.log(edit)

console.log('input', input)
  const deleteHandler = (e) => {
    setData(data.filter(el => el.id !== card.id))
  }

  const submitUpdate = value => {
    updateTodo(edit.id, value);
    setEdit({
      id: null,
      title: '',
    position: '',
    price: ''
    });
  };
  const updateTodo = (dataId, newValue) => {
    if (!newValue.text || /^\s*$/.test(newValue.text)) {
      return;
    }
    const newEdit = data.map(item => {
      if (item._id === dataId) {
      item = newValue
      } 
        return item
    })
    setData(newEdit)
  };

  const handleChange = e => {
    const value = e.target.value;
    console.log('value', value)
    setInput({ [e.target.name]: value });
  };

  const handleSubmit = e => {
    e.preventDefault();

    submitUpdate(input);
    setInput('');
  };



const editHandle = (e) => {
  return (
      <form >
      <>
          <input
            placeholder='Update your item'
            value={input}
            onChange={handleChange}
            name='title'
           // ref={inputRef}
            className='todo-input edit'
          />
          <button onClick={handleSubmit} className='todo-button edit'>
            Update
          </button>
         </>
         </form>
    );
}

  const updateButton = (e) => {
    if (!edit._id) {
     return (
     <button
          onClick={() => setEdit({ id: card._id, title: card.title })}
          className='edit-icon'
       > update</button>
  )
  }
}



  return (
    <div className="col-md-4 col-sm-6">
      <div className="card" style={{ borderColor: card.website ? "" : "red" }}>
        <div className="card-header">
          <img style={{ maxWidth: "100%" }} src={card.img} alt={card.name} />
        </div>
        <div className="card-body">
         {edit._id ? editHandle() : <h2 name='title' >{card.title}</h2>}{updateButton()}
          <h5>{card.position}</h5>
          {edit._id ? editHandle() : <div>{card.price}</div>}{updateButton()}
          <div></div>
          <div></div>
        </div>

        <button onClick={deleteHandler} className="trash-btn">
                    <i className="fas fa-trash"></i>
        </button>
      </div>
    </div>
  );
};

export default Cards;
