import React, { useRef, useState, useEffect,useContext } from "react";
import Tilt from "react-parallax-tilt";
import HartsLove from "./HartsLove";
import { Link, useParams } from "react-router-dom";
import axios from "axios";
import { prodactsConext } from "../context/contextData";

function Card3D({ el }) {


  const [like, setlike] = useState(false)

  const imgColor = el.img.map(imeEl => imeEl.color.Vibrant)
  const { user} = useContext(prodactsConext);



  const likes = (postId) => {
    let data = {like: postId }

    axios.put(`http://localhost:3001/products/${el._id}/like`, data).then((result) => {
          const checkLike = result.likes.some(item => item == user.user._id)
    console.log("checkLike",checkLike)
    if (checkLike) {
      setlike(true)
    }
      console.log("result", result.data);
    })
  };




  return (
    <Tilt className="TiltCard3D"   glareEnable={false} glareMaxOpacity={0.8} glareColor="#ffffff" glarePosition="bottom" glareBorderRadius="20px">
      <div class="card3D" style={{ color: imgColor[0] }} >
       {el.img.map(imeEl => <img src={"http://localhost:3001/" + imeEl.original} alt=""/>).slice(0,1)} 
       {el.img.map(imeEl => <div className="shadow3d" style={{ "backgroundImage": `url(${"http://localhost:3001/" + imeEl.thumb})` }}/>).slice(0,1)} 
              <div class="inner-card" >
          <h2>{el.price}</h2>
          <h3 >{el.title}</h3>
          <i onClick={() => {
            likes(user.user._id)
            }
          }  class={like ? "fas fa-heart" : "far fa-heart"}></i>
        </div>
        <span >
        </span>
        <HartsLove like={like}/>
        </div>
    </Tilt>
  );
}

export default Card3D;
