import { React,useEffect, useReducer } from "react";
import { basketReducer } from "../reducers/reducer";
import BasketList from "./BasketList";


const BaskeApi = () => {
  const [oneItem, dispatch] = useReducer(basketReducer, {items:[], total:0});



  return (
    <div>
      <div className="container">
        <div className="row">
          <h1 className="text-center">Basket</h1>
          {oneItem.map((el) => {
            return <BasketList key={el._id} oneItem={el} dispatch={ dispatch}/>;
          })}
        </div>
      </div>
    </div>
  );
};

export default BaskeApi;
