import React, { useRef, useState, useEffect, useContext } from "react";
import Tilt from "react-parallax-tilt";
import HartsLove from "./HartsLove";
import { Link, useParams } from "react-router-dom";
import axios from "axios";
import { prodactsConext } from "../context/contextData";

function Card3D({ el }) {
  const [like, setlike] = useState(false);
  const imgColor = el.img.map((imeEl) => imeEl.color.Vibrant);
  const { user } = useContext(prodactsConext);

  useEffect(() => {
    const checkLike = el.likes.some((item) => item == user.user._id);
    if (checkLike) {
      setlike(true);
    } else {
      setlike(false);
    }
  }, []);

  const likes = (postId) => {
    let data = { like: postId };

    axios
      .put(`http://localhost:3001/products/${user.user._id}/like`, data)
      .then((result) => {
        console.log("result", result.data);
        const checkLike = result.data.likes.some((item) => item == el._id);
        console.log(checkLike)
        if (checkLike){
          setlike(true);
        } else {
          setlike(false);
        }
      });
  };

  return (
    <Tilt
      className="TiltCard3D"
      glareEnable={false}
      glareMaxOpacity={0.8}
      glareColor="#ffffff"
      glarePosition="bottom"
      glareBorderRadius="20px"
    >

      <div className="card3D" style={{ color: imgColor[0] }}>
      <Link to={`item/${el._id}`}>
        {el.img
          .map((imeEl) => (
            <img src={"http://localhost:3001/" + imeEl.original} alt="" />
          ))
          .slice(0, 1)}
        {el.img
          .map((imeEl) => (
            <div
              className="shadow3d"
              style={{
                backgroundImage: `url(${
                  "http://localhost:3001/" + imeEl.thumb
                })`,
              }}
            />
          ))
            .slice(0, 1)}
           </Link>
        <div className="inner-card">
          <div>{el.price}</div>
          <div className="card3DFotter">
            <div className="card3D-title">{el.title}</div>
            <div className="card3D-likeCount">watchers {el.likeCount}</div>
            <i
              onClick={() => {
                likes(el._id);
              }}
              className={el.loggedInUserLiked > 0 ? "card3D-icon fas fa-heart" : "far fa-heart"}
            ></i>
          </div>
        </div>

        <HartsLove like={like} />

      </div>

    </Tilt>
  );
}

export default Card3D;
