import {React, useReducer} from 'react'

export const init = {
    lodaing: true,
    error: '',
    data:{}
}

 export const reducer = (state, action) => {
    switch (action.type) {
        case 'FATCH_SUCCESS':
            return {
                loading: false,
                oneItem: action.payload,
                errot:''
            }
        case 'FATCH_ERROR':
            return {
                loading: false,
                error: '',
                oneItem:{}
            }
        default:
           return state
    }
}


export default reducer
