import React, { useEffect, useState } from "react";
import Cards from "./cards";
import axios from "axios";
import {
  Route,
  Link
} from "react-router-dom";

function FetchingData() {

  const [data, setData] = useState([]);
  const [favorate, setfavorate] = useState()
const [btnFav, setbtnFav] = useState(false)

useEffect(() => {
  getProduct();
  getSaveLocal()

},[])


  useEffect(() => {
    if (btnFav === true) {
    setfavorate(data.filter(event => event.completed === true))
  }

}, [data])

  useEffect(() => {
    saveLocal()
  },[favorate])

  const addToWachList = (card) => {
    setbtnFav(true)
    setData(data.map(el => {

      if (el._id === card._id) {
          return {
              ...el, completed: !el.completed
          }
      }
      return el
    }))

  }




  const getSaveLocal = () => {
    if (localStorage.getItem('favorate') === null) {
        localStorage.setItem('favorate', JSON.stringify([]))
    } else {
        let favLocal = JSON.parse(localStorage.getItem('favorate'));
        setfavorate(favLocal);
    }
  }

  const saveLocal  = () => {
    localStorage.setItem('favorate', JSON.stringify(favorate));
}

console.log('favorate',favorate)

  async function getProduct() {
    try {
      let response = await fetch(`http://localhost:3001/products`, {
        method: "GET",
      });
      const responseData = await response.json();
      console.log(responseData);
      setData(responseData);
    } catch (error) {
      handleError(error);
    }


    if (data.error) handleError(data.error);
    return data;
  }

  function handleError(error) {
    error = error.message;
    console.log(error);
  }


  return (
    <div className='container'>
    <div className="row">
      <Link to="/sellerForm">Selling</Link>
      <h1 className="text-center">Our online shop</h1>
      {data.map((event) => {
        return <Cards addToWachList={addToWachList} favorate={favorate} setfavorate={setfavorate}  key={event._id} card={event} data={data} setData={ setData}/>;
      })}
      </div>
      </div>
  );
}

export default FetchingData;
