import {React, useEffect} from 'react'
import axios from 'axios'
import {putReq} from './useFetch'

const AddToWatchList = ({ data, dispatch }) => {

    useEffect(() => {
        putReq(body, data._id)
    }, [data])

    const addToWachList = () => {  
        dispatch({type:'TOGGLE_WATCH', payload: data})
    }

    const completesHandle = () => {
        axios.put(`http://localhost:3001/products/${data._id}`, data).then((result) => {
            console.log('watchBtn', result.data)
        });
    }

    return (
        <div>
            <button className='btn' onClick={() => addToWachList()}>{data.completed == false ? 'Add to Watch' : 'Remove from watch List'}</button>
        </div>
    )
}

export default AddToWatchList
