import { React, useState, useEffect, useContext } from "react";
import Edit from "./Edit";
import Buy from "../basket/Buy";
import { auth, getReq, likes } from "../api/rest-helper";
import { useParams } from "react-router-dom";
import { prodactsConext } from "../context/contextData";
import CheckLogIn from "../CheckLogIn";
import "./item.css";
import ImageGallery from "react-image-gallery";
import "react-image-gallery/styles/css/image-gallery.css";

const Item = () => {
  const { id } = useParams();
  const [edit, setEdit] = useState(false);
  const { user, dispatch, watch, product } = useContext(prodactsConext);
  const [images, setImages] = useState([]);
  const [count, setCount] = useState(0);
  const [like, setlike] = useState(false);


  useEffect(() => {
    auth();
    getReq(id).then((result) => {
      console.log("item", result.data);
      dispatch({ type: "RECENT_ITEM", payload: result.data });
      dispatch({ type: "FATCH_SUCCESS_ITEM", payload: result.data });
    });
  }, [id, edit]);

  useEffect(() => {
    setImages(
      product.img
        ? product.img.map((url) => ({
            original: `http://localhost:3001/${url.original}`,
            thumbnail: `http://localhost:3001/${url.original}`,
            originalClass: "featured-slide",
            thumbnailClass: "featured-thumb",
            description: "",
          }))
        : ""
    );
  }, [product]);




  const likeHandler = ( postId, userId) => {

    likes(postId, userId).then((response) => {
      console.log("response", response.data)
      const hasLike = response.data.likes.some((item) => item == postId);
      if (hasLike) {
         setlike(true)
        } else {
          setlike(false)
        }
    })
  }

  const colors = product.img && product.img.map((el) => el);

  return (
    <div className="item">
      <div className="gallery">
        {images ? (
          <ImageGallery
            items={images}
            additionalClass="app-image-gallery "
            onSlide={(e) => setCount(e)}
          />
        ) : null}
      </div>
      <div
        className="appShadowGallery"
        style={{
          backgroundColor: product.img ? colors[count].color.Muted : "black",
        }}
      />
      {edit ? (
        <Edit key={product._id} setEdit={setEdit} dispatchOneItem={dispatch} />
      ) : (
        <div className="item-info">
          <h1>{product.title}</h1>
          <div className="item-body">


              <div className="item-price">
              <h5>{product.price}<span>€</span></h5>
                <div>
                   <button onClick={() => dispatch({ type: "DECROMENT_ITEM" })}>
                {" "}
                -{" "}
              </button>
              <div>Quantity: {product.quantity}</div>
              <button onClick={() => dispatch({ type: "INCROMENT_ITEM" })}>
                {" "}
                +{" "}
              </button>
                </div>

            </div>
          </div>
          {user.loggedIn &&
          user.user &&
          product.owner &&
          product.owner._id == user.user._id ? (
            <button
              onClick={() => setEdit(!edit)}
              className=""
            >
              {" "}
              update
            </button>
          ) : (
            <Buy key={product._id} card={[product]} />
          )}
          <button
            className=""
            onClick={() => likeHandler(product._id, user.user)}
          >
            {like
              ? "Watched"
              : "add watch"}
          </button>
          <button
            className=""
            onClick={() => dispatch({ type: "ADD_ITEM", payload: product })}
          >
            add to cart
          </button>
        </div>
      )}
      <div className="item-descraption">
        <h3>Descraption</h3>
        <p>{product.description}</p>
      </div>
      <div className="item-specifics">
        <h5>Type</h5>
        <p>Lorem, ipsum.</p>
        <h5>Color</h5>
        <p>Lorem ipsum dolor sit.</p>
        <h5>Features</h5>
        <p>
          Lorem ipsum dolor sit amet consectetur adipisicing elit. Aliquid,
          necessitatibus.
        </p>
        <h5>MPN</h5>
        <p>Lorem, ipsum dolor sit amet consectetur adipisicing.</p>
        <h5>Connectivity</h5>
        <p>Lorem ipsum dolor sit.</p>
        <h5>Model</h5>
        <p>Lorem, ipsum.</p>
        <h5>Brand</h5>
        <p>Lorem ipsum dolor sit amet consectetur adipisicing elit.</p>
        <h5> Form Factor</h5>
        <p>Lorem, ipsum dolor.</p>
        <h5>Country/Region of Manufacture</h5>
        <p>Lorem, ipsum.</p>
        <h5>Manufacturer</h5>
        <p>Lorem ipsum dolor sit.</p>
        <h5> Number of Earpieces</h5>
        <p>Lorem, ipsum.</p>
      </div>
    </div>
  );
};

export default Item;
