import { React, useState, useEffect,useContext } from "react";
import Edit from "./Edit";
import Buy from "../basket/Buy";
import {  getReq } from "../api/rest-helper";
import { useParams } from "react-router-dom";
import { prodactsConext } from '../context/contextData'
import CheckLogIn from "../CheckLogIn";
import AddToWatch from "./AddToWatch"


const Item = () => {
  const { id } = useParams();
  const [edit, setEdit] = useState(false);
const [state, setState] = useState(false)
  const { dispatch1,user,dispatchOneItem,oneItem,dispatchWatch , watchItem} = useContext(prodactsConext)


  useEffect(() => {
    let userLocal = JSON.parse(localStorage.getItem('user'));
      getReq(id).then((result) => {
        dispatchOneItem({ type: "FATCH_SUCCESS", payload:result.data });
    });
  }, [id, edit]);




console.log('item', oneItem)

  return (
    <div className="container">
      <div className="row">

        {edit ? (
          <CheckLogIn  component={() => <Edit key={oneItem._id} setEdit={setEdit} dispatchOneItem={dispatchOneItem} />}/>      

        ) : (

            <div className="col-md-4 col-sm-6">
              <h1>Item </h1>
            <div
              className="card"
              style={{ borderColor: oneItem.website ? "" : "red" }}
            >
              <div className="card-header">
                {oneItem.img == undefined
                  ? ""
                  : oneItem.img.map((e) => (
                      <img
                        style={{ maxWidth: "100%" }}
                        src={"http://localhost:3001/" + e}
                        alt={oneItem.name}
                      />
                    ))}
              </div>
              <div className="card-body">
                <h2>{oneItem.title}</h2>
                <h5>{oneItem.category}</h5>
                  <div>{oneItem.price}</div>
                  <div>{oneItem.quantity}</div>
              </div>
                {user.loggedIn && user.user && oneItem.owner && oneItem.owner._id == user.user._id ?  <button
                onClick={() => setEdit(!edit)}
                className="btn btn-secondary"
              >
                {" "}
                update
              </button>
               : <Buy key={oneItem._id} card={[oneItem]} />}

              <button className="btn"onClick={() => setState(true)}>add
                </button>
               { state ? <CheckLogIn component={() => <AddToWatch key={oneItem._id} oneItem={oneItem} dispatchWatch={dispatchWatch} watchItem={watchItem} />}/> :'' }
                <button
                className="btn"
                onClick={() =>
                  dispatch1({ type: "ADD_ITEM", payload: oneItem, })
                }
              >
                add to cart
                </button>
                <button onClick={()=> dispatchOneItem({type: 'DECROMENT'})}> - </button>
                <button onClick={() => dispatchOneItem({ type: 'INCROMENT'})}> + </button>

            </div>
          </div>

        )}
      </div>
    </div>
  );
};

export default Item;
