export const reducer = (oneItem, action) => {
  switch (action.type) {
    case "FATCH_SUCCESS":
      return action.payload;
    case "FATCH_ERROR":
      return {error: 'Somthing went wrong!'}
    default:
      return oneItem;
  }
};

export const watchReducer = (oneItem, action) => {
  switch (action.type) {
    case "GETS_DATA_COMPLETED":
      return action.payload.filter((event) => event.completed === true);
    default:
      return oneItem;
  }
};

export const itemReducer = (oneItem, action) => {
  switch (action.type) {
    case "FATCH_SUCCESS":
      return action.payload;
    case "TOGGLE_WATCH":
      return {
        ...oneItem,
        completed: !action.payload.completed,
      };
    case "DELETE_ITEM":
      return action.payload;
    case "UPDATE_ITEM":
      return action.payload;
    default:
      return oneItem;
  }
};
