import React,{useState, useEffect} from 'react'
import './item.css'

const Image = ({ el, i }) =>
{

	return (
		<>
			<div className="item">
			{el && el.img ? el.img.map(img => <img className="current" src={"http://localhost:3001/" + img.original} alt={el.title} />).slice(0,1) :''}
			{el && el.img ? el.img.map(img => <img className="thumb" src={"http://localhost:3001/" + img.original} alt={el.title} />) :''}
     </div>
			</>
	);	
}

export default Image
