import { React,useContext } from "react";
import BasketList from "./BasketList";
import Buy from './Buy'
import {prodactsConext} from  '../context/contextData'


const BaskeApi = () => {

  const { basket, dispatch1 } = useContext(prodactsConext)

  const asyncLocalStorage = {
    setItem: function (key, value) {
        return Promise.resolve().then(function () {
            localStorage.setItem(key, JSON.stringify([value]));
        });
    },
    getItem: function (key) {
        return Promise.resolve().then(function () {
            return localStorage.getItem(key);
        });
    }
  };

  asyncLocalStorage.setItem('mykey', basket.items).then(function () {
    return asyncLocalStorage.getItem('mykey');
  }).then(function (value) {
    dispatch1({type:"GET_LOCAL", payload: value})
    console.log('Value has been set to:', value);
});




  return (
    <div><h1 className="text-center">Basket</h1>
       {basket.items.length ?  <div className="container">
        <div className="row">

          {basket.items.map((el) => {
            return <BasketList key={el._id} oneItem={el} dispatch1={dispatch1} basket={basket} />;
          })}
        </div>
      <div className='text-danger'>Total Price: {basket.total}</div>
        <Buy card={basket.items} />
      </div> : <h3 className="text-center">your Basket is Empty</h3>}
    </div>

  );
};


export default BaskeApi;
