import {React, useReducer} from 'react'

const oneItem = {
    completed :false
}

 export const reducer = (oneItem, action) => {
    switch (action.type) {
        case 'FATCH_SUCCESS':
            return (
                 action.payload
            )
        case 'TOGGLE_WATCH':
            return {
                ...oneItem, completed: !completed
            }


        default:
           return oneItem
    }
}


export default reducer
