import React, { useContext,useState } from "react";
import { Link } from "react-router-dom";
import { prodactsConext } from "../context/contextData";
import Search from "../actions/Search";

const NavBar = () => {
  const { user, dispatchUser } = useContext(prodactsConext);
  const [open, setOpen] = useState(false)
  return (
    <nav class=" navbar navbar-expand-lg">
      <a class="navbar-brand" href="#" data-abc="true">
        Online Shop
      </a>
      <button
        class="navbar-toggler"
        type="button"
        data-toggle="collapse"
        data-target="#navbarColor02"
        aria-controls="navbarColor02"
        aria-expanded="false"
        aria-label="Toggle navigation"
        onClick={()=> setOpen(true)}
      >
        <span>
        <i className={open ? "fas fa-chevron-circle-up" : "fas fa-chevron-circle-down"}></i>
        </span>
      </button>
      <div class="collapse navbar-collapse" id="navbarColor02">
        <ul class="navbar-nav mr-auto">
          <li class="nav-item active">
            {" "}
            <a class="nav-link" href="#" data-abc="true">
              Home <span class="sr-only">(current)</span>
            </a>{" "}
          </li>
          <li class="nav-item">
            <Link class="nav-link" to="/sellerForm">
              Sell Somthing
            </Link>
          </li>
          <li class="nav-item">
            {" "}
            <Link class="nav-link" to="/watchList">
              Watched List
            </Link>
          </li>
          <li class="nav-item">
            {" "}
            <Link class="nav-link" to="/basketList">
              Basket List
            </Link>
          </li>
          <li class="nav-item">
            {" "}
            <Link class="nav-link" to="/register">
              Register
            </Link>
          </li>
          <li class="nav-item">
            {" "}
            {user.loggedIn && user.user ? (
              <a
                class="nav-link"
                style={{ cursor: "pointer" }}
                onClick={() => {
                  dispatchUser({ type: "LOGOUT_USER", payload: "" });
                  let keysToRemove = ["user", "x-auth"];
                  for (let key of keysToRemove) {
                    localStorage.removeItem(key);
                  }
                }}
              >
                logout
              </a>
            ) : (
              <Link class="nav-link" to="/login">
                Login
              </Link>
            )}{" "}
          </li>
          <li class="nav-item">
            {" "}
            {user.loggedIn && user.user ? (
              <a class="nav-link">
                Hi!.. {user.user.firstName} {user.user.lastName}
              </a>
            ) : (
              ""
            )}{" "}
          </li>
        </ul>
      </div>
      <Search />
    </nav>
  );
};

export default NavBar;
