import { reducer,basketReducer,usersReducer } from "../reducers/reducer";

export const prodactsConext = createContext();

export const ProductsProvider = props => {
    const [data, dispatch] = useReducer(reducer, []);
    const [basket, dispatch1] = useReducer(basketReducer, { items: [], total: 0 });
    const [user, dispatchUser] = useReducer(usersReducer, []);


    return (
        <prodactsConext.Provider value={{data, dispatch, basket, dispatch1, user, dispatchUser }}>
           {props.children}
        </prodactsConext.Provider >
   );
}
