import {React, useState} from 'react'
import axios from "axios";


const Edit = ({ card, setData,  setEdit, data}) => {

    const [input, setInput] = useState(false);
    const [gitOneData, setGitOneData] = useState(false);

    if ( !gitOneData ){
        setGitOneData(true)
       axios.get(`http://localhost:3001/products/${card._id}`)
        .then( result => setInput(result.data) );
      }

    const handleChange = e => 
        setInput({...input,
       [e.target.name] : e.target.value
    }

        );

    const handleUpdate = e => {
    e.preventDefault();
    axios.put(`http://localhost:3001/products/${card._id}`, input )
        .then(result => {
            setData(result.data)
            console.log('result',result.data)
    }  )
        setEdit(false)
    };

const handleDelete = (e) => {
    e.preventDefault();
    axios.delete(`http://localhost:3001/products/${card._id}`)
    .then(result => {
      console.log("deleted data", result)
          setData(result.data)

      });

}


    console.log('input', input);



    return (
        <>
     <div className='row'>      
    <div key={card._id} className="col-md-4 col-sm-6">
      <div className="card" style={{ borderColor: card.website ? "" : "red" }}>
        <div className="card-header">
          <img style={{ maxWidth: "100%" }} src={card.img} alt={card.name} />
        </div>
        <div className="card-body">
        <input
            placeholder='update your data'
            value={input.title}
            onChange={handleChange}
            name='title'
           // ref={inputRef}
            className='todo-input edit'
        />
          <h5>{card.position}</h5>
          <input
            placeholder='update your data'
            value={input.price}
            onChange={handleChange}
            name='price'
           // ref={inputRef}
            className='todo-input edit'
        />
          <div></div>
          <div></div>
        </div>


      </div>
    </div>


          <button className="btn btn-warning" onClick={handleUpdate}>
            Update
            </button>
                <button className="btn btn-danger" onClick={handleDelete}>delete</button>
                </div> 
        </>
    )
}

export default Edit
