export const reducer = (state, action) => {
  switch (action.type) {
    case "FATCH_SUCCESS":
      return action.payload;
    default:
      return state;
  }
};

export const watchReducer = (state, action) => {
  switch (action.type) {
    case "GETS_DATA_COMPLETED":
      return action.payload.filter((event) => event.completed === true);
    default:
      return state;
  }
};

export const basketReducer = (state, action) => {
  switch (action.type) {
    case "GETS_DATA_BASKET":
      return action.payload.filter((event) => event.basket === true);
    case "INCROMENT":
      const { cart } = state;
      cart.forEach(item =>{
        if (item._id === action.payload) {
            console.log(item)
              item.price += 1;
          }
      })
        return {count: cart };
    default:
      return state;
  }
};


export const itemReducer = (state, action) => {
  switch (action.type) {
    case "FATCH_SUCCESS":
      return action.payload;
    case "TOGGLE_WATCH":
      return {
        ...state,
        completed: !action.payload.completed,
      };
      case "TOGGLE_BASKET":
        return {
          ...state,
          basket: !action.payload.basket,
        };
    case "DELETE_ITEM":
      return action.payload;
    case "UPDATE_ITEM":
      return action.payload;
    default:
      return state;
  }
};

export const countReducer = (state, action) => {
  switch (action.type) {
      case "INCROMENT":
        return {count: action.payload * state.count+1 };
    default:
      return state;
  }
};
