import { React, useState } from "react";
import { getReq, putReq, deleteReq } from "./rest-helper";
import { Redirect, useParams } from "react-router-dom";

const Edit = ({ setEdit, dispatch }) => {
  const [input, setInput] = useState(false);
  const [gitOneData, setGitOneData] = useState(false);
  const [redirect, setredirect] = useState(false);
  const { id } = useParams();

  if (!gitOneData) {
    setGitOneData(true);
    getReq(id).then((result) => setInput(result));
  }

  const handleChange = (e) =>
    setInput({ ...input, [e.target.name]: e.target.value });

  const handleUpdate = (e) => {
    e.preventDefault();
    putReq(input, id).then((result) =>
      dispatch({ type: "UPDATA_ITEM", payload: result})
    );
    setEdit(false);
  };

  const handleDelete = (e) => {
    e.preventDefault();
    deleteReq(id).then((result) =>
      dispatch({ type: "DELETE_ITEM", payload: result})
    );
    setredirect(true);
  };

  return (
    <>
      {redirect ? <Redirect to="/" /> : ""}
      <div className="col-md-4 col-sm-6">
        <div className="card" style={{ borderColor: "" ? "" : "red" }}>
          <div className="card-header">
            <img style={{ maxWidth: "100%" }} src="#" alt="" />
          </div>
          <div className="card-body">
            <input
              placeholder="update your data"
              value={input.title}
              onChange={handleChange}
              name="title"
              // ref={inputRef}
              className="todo-input edit"
            />
            <input
              placeholder="update your data"
              value={input.category}
              onChange={handleChange}
              name="category"
              // ref={inputRef}
              className="todo-input edit"
            />

            <input
              placeholder="update your data"
              value={input.price}
              onChange={handleChange}
              name="price"
              // ref={inputRef}
              className="todo-input edit"
            />
            <div></div>
            <div></div>
          </div>
        </div>
        <button className="btn btn-warning" onClick={handleUpdate}>
          Update
        </button>
        <button className="btn btn-danger" onClick={handleDelete}>
          delete
        </button>
      </div>
    </>
  );
};

export default Edit;
