import React, { useState, useEffect } from "react";
import "./item.css";
import ImageGallery from 'react-image-gallery';

const Image = ({el }) => {
	const [images, setImages] = React.useState(null);

   useEffect(() => {
setImages(
	el.img.map(url => ({
	  original: `${url}=w1024`,
	  thumbnail: `${url}=w100`
	}))
  );
   }, [])       


  return (
	  <>
<ImageGallery items={images} />

    </>
  );
};

export default Image;
