import { useReducer, useEffect } from "react";
import { reducer } from "./reducer";
import axios from "axios";
import { API_ROOT } from "./service-info";

export const useFetch = (id) => {
  const [oneItem, dispatch] = useReducer(reducer, false);

  useEffect(() => {
    axios
      .get(`${API_ROOT}/${id}`)
      .then((result) =>
        dispatch({ type: "FATCH_SUCCESS", payload: result.data })
      );
  }, []);

  return [oneItem, dispatch];
};

export const putReq = (body, id) => {
  axios.put(`${API_ROOT}/${id}`, body).then((result) => {
    return result;
  });
  return;
};

export const postReq = (body) => {
  axios.post(`${API_ROOT}/`, body).then((result) => {
    return result;
  });
  return;
};

export const getReq = (id) => {
  axios.get(`${API_ROOT}/${id}`).then((result) => {
    return (result.data);
  });
};
