import { React, useContext, useEffect, useState } from "react";
import { prodactsConext } from "../context/contextData";
import Cards from "./Cards";
import { getsReq } from "../api/rest-helper";
import RecentList from "../recent/RecentList";
import DescountsList from "../discounts/DiscountsList";
import Slider from "./Slider";
function HomeApi() {
  const { dispatch, products, user } = useContext(prodactsConext);

  // Creat Fack User if there is no real User
  if (!user.user) {
    const _id = Date.now();
    dispatch({ type: "FACK_USER", payload: { _id: _id } });
  }

  useEffect(() => {
    getsReq().then((result) => {
      dispatch({ type: "FATCH_SUCCESS", payload: result.data });
    });
  }, []);

  return (
    <div className="container2">
      <Slider />
      <div className="sections mt-2 mb-2">
        <img width="100%" src="/images/Apple/apple3.jpg" alt=""/>
      </div>
      <div className="d-flex mt-2">
        <div className="two">
           <img width="100%" src="/images/Apple/apple5.jpg" alt=""/>
        </div>
        <div className="one">
          <h2>iPhone 12</h2>
        <img width="100%" src="/images/Apple/apple4.jpg" alt=""/>
       </div>
      </div>
      <div className="d-flex mt-2">
        <div className="two">
           <img width="100%" src="/images/svg/Living_room_Wall_14.jpg" alt=""/>
        </div>
        <div className="one">
        <img width="100%" src="/images/svg/13.jpg" alt=""/>
       </div>
      </div>

      <div className="row">
        {products.length
          ? products.map((event) => {
              return <Cards key={event._id} card={event} dispatch={dispatch} />;
            })
          : "loading"}
        <RecentList />
        <DescountsList />
      </div>
    </div>
  );
}

export default HomeApi;
