import React,{useState, useContext} from 'react'
import axios from "axios";
import { Redirect } from "react-router-dom";
import {prodactsConext}from '../context/contextData'


const Login = () => {
    const [inputText, setInputText] = useState([]);
    const [redirect, setRedirect] = useState(false);
   const {user, dispatchUser} = useContext(prodactsConext)

    const handleChange = (e) => {
           setInputText({...inputText,[e.target.name]: e.target.value,});
      };

    const handleSubmit = (e) => {
       // axios.defaults.headers.common['x-auth'] = token 
        axios.post(`http://localhost:3001/users/login`, inputText,).then((result) => {
            console.log("result", result);
            dispatchUser({ type: "LOGIN_SUCCESS", payload: result.data })
            const token = result.headers['x-auth']
            console.log('token', token)
            if (result.status == 200) {
                setRedirect(true)
            }

        })
            .catch(error => {
          console.log(error)
        })

    }



    return (
        <div>
        {redirect ? <Redirect to="/" /> : ""}
             <div className='container'>
         <h2 className='text-center'>Login</h2>
      <form action="#">
        <label htmlFor="email">
          <input
            id="email"
            placeholder="email"
            value={inputText.email}
            type="text"
            name="email"
            onChange={handleChange}
          />
        </label>
        <label htmlFor="password">
          <input
            id="password"
            placeholder="password"
            value={inputText.password}
            type="text"
            name="password"
            onChange={handleChange}
          />
        </label>
              <button onClick={handleSubmit} className="red" type="button">
          <i className="icon ion-md-lock"></i>Login
        </button>
              </form>

    </div>
        </div>
    )
}

export default Login
