import AddToBasket from "./AddToBasket";
import AddToWatchList from "./AddToWatchList";



const Cards = ({ card, data, setData,}) => {



  return (
    <>
   <Link to={`item/${card._id}`}>
          <div key={card._id} className="col-md-4 col-sm-6">
      <div className="card" style={{ borderColor: card.website ? "" : "red" }}>
            <div className="card-header">
            {card.img.map(e=> <img style={{ maxWidth: "100%" }} src={'http://localhost:3001/'+e} alt={card.name} />)}

        </div>
        <div className="card-body">
         <h2 >{card.title}</h2>
          <h5>{card.type}</h5>
          <div>{card.price}</div>
          <div></div>
          <div></div>
            </div>



      </div>
      </div>
   </Link>
      <Route path="/item">
          <Item/>
      </Route>
      </>
  );
};

export default Cards;
