import { React, useState } from "react";
import axios from "axios";
import {getReq, putReq} from './useFetch'
import {
  BrowserRouter as Router,
  Switch,
  Route,
  Link,
  useRouteMatch,
  useParams,
} from "react-router-dom";

const Edit = ({ card, setData, setEdit, data }) => {
  const [input, setInput] = useState(false);
  const [gitOneData, setGitOneData] = useState(false);

  const { id } = useParams();

  if (!gitOneData) {
      setGitOneData(true);
      getReq(id).then(result => {setInput(result)})
  }

  const handleChange = (e) =>
    setInput({ ...input, [e.target.name]: e.target.value });

  const handleUpdate = (e) => {
    e.preventDefault();
    putReq(input, id).then(newData => setData(newData) )
      setEdit(false);

  };

  const handleDelete = (e) => {
    e.preventDefault();
    axios.delete(`http://localhost:3001/products/${id}`).then((result) => {
      console.log("deleted data", result);
      setData(result.data);
    });
  };


  return (
    <>
      <div key={card._id} className="col-md-4 col-sm-6">
        <div
          className="card"
          style={{ borderColor: card.website ? "" : "red" }}
        >
          <div className="card-header">
            <img style={{ maxWidth: "100%" }} src={card.img} alt={card.name} />
          </div>
          <div className="card-body">
            <input
              placeholder="update your data"
              value={input.title}
              onChange={handleChange}
              name="title"
              // ref={inputRef}
              className="todo-input edit"
            />
            <input
              placeholder="update your data"
              value={input.category}
              onChange={handleChange}
              name="category"
              // ref={inputRef}
              className="todo-input edit"
            />

            <input
              placeholder="update your data"
              value={input.price}
              onChange={handleChange}
              name="price"
              // ref={inputRef}
              className="todo-input edit"
            />
            <div></div>
            <div></div>
          </div>
        </div>
        <button className="btn btn-warning" onClick={handleUpdate}>
          Update
        </button>
        <button className="btn btn-danger" onClick={handleDelete}>
          delete
        </button>
      </div>
    </>
  );
};

export default Edit;
