import React,{useContext} from "react";
import { Link } from "react-router-dom";
import { prodactsConext } from "../context/contextData";

const NavBar = () => {
    const { data, dispatch, user, dispatchUser } = useContext(prodactsConext);
  return (
    <div className="navbar container">
          <div >hello pretty maha</div>
          {user.user ? (
          <div
            style={{ cursor: "pointer" }}
            onClick={() => {
              dispatchUser({ type: "LOGOUT_USER", payload: '' })
              let keysToRemove = ["user", "x-auth"];
              for (let key of keysToRemove) {
                localStorage.removeItem(key);
              }
            }
          }
          >
            logout
          </div>
        ) : (
          <Link to="/login">Login</Link>
          )}
          {user.user ? (
          <div className="text-primary">
            Hi!.. {user.user.firstName} {user.user.lastName}
          </div>
        ) : (
          ""
        )}
          <input className="navInput" type="text"/>
    </div>
  );
};

export default NavBar;
