import { React,useContext,useEffect } from "react";
import BasketList from "./BasketList";
import Buy from './Buy'
import { prodactsConext } from '../context/contextData'



const BaskeApi = () => {

  const { basket, dispatch1 ,user} = useContext(prodactsConext)




  return (
    <div><h1 className="text-center">Basket</h1>
       {basket.items ?  <div className="container">
        <div className="row">

          {basket.items.map((el) => {
            return <BasketList key={el._id} oneItem={el} dispatch1={dispatch1} basket={basket} user={user} />;
          })}
        </div>
      <div className='text-danger'>Total Price: {basket.total}</div>
        <Buy card={basket.items} />
      </div> : <h3 className="text-center">your Basket is Empty</h3>}
    </div>

  );
};


export default BaskeApi;
