import { React } from "react";
import { Link } from "react-router-dom";

const WatchList = ({ product, dispatch }) => {
  return (
    <>
      <div key={product._id} className="col-md-4 col-sm-6">
        <div
          className="card"
          style={{ borderColor: product.website ? "" : "red" }}
        >
          <Link to={`item/${product._id}`}>
            <div className="card-header">
              {product.img == undefined
                ? ""
                : product.img.map((e) => (
                    <img
                      key={product._id}
                      style={{ maxWidth: "100%" }}
                      src={"http://localhost:3001/" + e}
                      alt={product.name}
                    />
                  )).slice(0, 1)}
            </div>
            <div className="card-body">
              <h2>{product.title}</h2>
              <h5>{product.category}</h5>
              <div>{product.price}</div>
            </div>
          </Link>
          <button
            onClick={() =>
              dispatch({ type: "DELETE_ITEM", payload: product })
            }
          >
            Delete
          </button>
        </div>
      </div>
    </>
  );
};

export default WatchList;
