import React, { useState, useEffect } from "react";
import axios from "axios";
import {
  Route,
  Link
} from "react-router-dom";

const SellerForm = () => {
  const [inputText, setInputText] = useState(false);

  const [addProduct, setAddProduct] = useState(false);
	const [Message	, setMessage] = useState([]);
  const [isData, setisData] = useState(false)
 // const [img, setimage] = useState([])


const handleImage = (e) => {
  let img = [...e.target.files].map(el => {
   return el
  });
  setInputText({
    ...inputText, [e.target.name]:img }

  );

}


console.log(inputText)
  const handleChange = (e) => {



    setInputText({
      ...inputText,
      [e.target.name]: e.target.value
    });

  };


/*
  const handleSubmit = (e) => {
    e.preventDefault();
    setAddProduct(inputText)
    axios.post(`http://localhost:3001/products/`, inputText )
    .then(result => {
        console.log('result',result.data)
    })
    */

  const handleSubmit = (e) => {
    e.preventDefault();
   const onData = new FormData();
    for(let key in inputText){
      if (key == 'img') {
        console.log('img', typeof inputText[key])
        if(typeof inputText[key] == 'object') onData.append(key, inputText[key], inputText[key].name)
      } else {
        onData.append(key, inputText[key])
      }
    }
    console.log('ondata',onData)
    axios.post(`http://localhost:3001/products/`, onData )
    .then(result => {
        console.log('result',result.data)
    })

/*
    if (isData === true) {
     setAddProduct(inputText);
      setInputText({
        title: "",
        type: "",
        price: "",
      })
      setisData(false)
      setMessage({ text: 'The data has been sent successfully' })
    } else {
     setMessage({ text: 'add a prodact' })
    }
	 setTimeout(() => {
    setMessage([])
   }, 2000);
   */
  };







  return (
    <div className='container'>
      <Link to="/">Home</Link>
      <form id="loginForm" method="POST" action="/#" >
        <legend>
          <fieldset>Seller Form</fieldset>
          <div className="formBox">
            <label for="name">Title</label>
					  <input
						 value={inputText.title} 
              className="email"
              type="text"
              autocomplete="off"
              name="title"
              onChange={handleChange}
            />
          </div>
          <div className="formBox">
            <label for="position">Type</label>
					  <input
						  value={inputText.type}
              class="pass"
              type="text"
              autocomplete="off"
              name="type"
              onChange={handleChange}
            />
          </div>
          <div class="formBox">
            <div className="formBox">
              <label for="price">Price</label>
						  <input
							  value={inputText.price}
                class="pass"
                type="text"
                autocomplete="off"
                name="price"
                onChange={handleChange}
              />
              <input type="file" name="img" onChange={handleImage}/>
            </div>
            <div class="formBox"></div>
            <button onClick={handleSubmit}>Submit</button>
          </div>
        </legend>
		  </form>
		  {Message.text}
    </div>
  );
};

export default SellerForm;
