import { React,useContext } from "react";
import axios from "axios";
import { prodactsConext } from '../context/contextData'
import Login from '../users/Login'

const Buy = ({ card }) => {

  const { user } = useContext(prodactsConext)

  const buyItem = () => {
    const order = {
      products: [],
    };

    card.forEach(element => {
      order.products.push({
        product: element._id,
        amount:element.quantity
      });
    });
   //  token login defult use one time
   // axios.defaults.headers.common['x-auth'] = token 

    console.log("order", card);
    axios.post(`http://localhost:3001/orders/`,order).then((result) => {
      console.log("result", result.data);
    });
  };

  return (
    <div className="col-md-4 col-sm-6">
      <div className="card-body">
      {!user.loggedIn ? <Login/> : <button className="btn-primary" onClick={buyItem}>
          Buy
        </button>}
      </div>
    </div>
  );
};

export default Buy;
