export const reducer = (state, action) => {
  switch (action.type) {
    case "FATCH_SUCCESS":
      return action.payload;
    default:
      return state;
  }
};

export const watchReducer = (state, action) => {
  switch (action.type) {
    case "GETS_DATA_COMPLETED":
      return action.payload.filter((event) => event.completed === true);
    default:
      return state;
  }
};

    export const basketReducer = (state = { total:0, items:[]}, action) => {
    switch (action.type) {
      case "ADD_ITEM":
        const hasItem = state.items.some(el => el._id === action.payload._id);

        if (hasItem) {
          // update item
          return {
            ...state,
            items: state.items.map(el => {
              if (el._id === action.payload._id) {
                return {
                  ...el,
                  quantity: el.quantity + action.payload.quantity
                }
              }
              return el; // <-- return current mapped element if no change
            }),
          };
        } else {
          // add item 
          const totalItem = totalprice(action.payload)
          return {
            ...state,
            items: state.items.concat(action.payload),
          };
        }


    case "INCROMENT":
      const updateItems = state.items.map(el => {
        if (el._id === action.payload._id) {
          return {...el, quantity: el.quantity +1}
          }
          return el
      })
      console.log('INCROMENT', updateItems)
      const totalsupdateItem = totalprice(updateItems)
      return {
        items: updateItems,
        total: totalsupdateItem
      }
      case "DECROMENT":
        const updateItemsminus = state.items.map(el => {
          if (el._id === action.payload._id) {
            if (el.quantity > 1) {
              return {...el, quantity: el.quantity -1}
            }
          }
          return el
        })
        const totalsupdateItemminus = totalprice(updateItemsminus)
        return {
          items: updateItemsminus,
          total: totalsupdateItemminus
        }
    case "GET_BASKET_LOCAL":
      console.log('local', action.payload)
     return action.payload

    default:
      return state;
  }
};

const totalprice = (item) => {
  var totals = 0;
      item.forEach(el => {
        totals += el.quantity * el.price
      })
  return totals
}



export const itemReducer = (state , action) => {
  switch (action.type) {
    case "FATCH_SUCCESS":
      const data = action.payload
      return {
        ...data,
        quantity: 1
      };
    case "TOGGLE_WATCH":
      return {
        ...state,
        completed: !action.payload.completed,
      };
      case "TOGGLE_BASKET":
        return {
          ...state,
          basket: !action.payload.basket,
        };
    case "INCROMENT":
      return {
        ...state,
        quantity: state.quantity + 1
      }
    case "DECROMENT":
      if (state.quantity > 1) {
         return {...state,
          quantity: state.quantity - 1
            };
      } else {
       return {...state, quantity: state.quantity}
      }
    case "DELETE_ITEM":
      return action.payload;
    case "UPDATE_ITEM":
      return action.payload;
    default:
      return state;
  }
};

export const usersReducer = (state, action) => {
  switch (action.type) {
    case "LOGIN_SUCCESS":
      const user = action.payload
      return {
        user,
        loggedIn:true
      };
    case "LOGOUT_USER":
      return action.payload
    case "PASS_WRONG":
      return action.payload
    case "EMAIL_WRONG":
      return action.payload
    case "GET_USER_LOCAL":
      return action.payload
    default:
      return state;
  }

}
