import { React, useState, useEffect,useReducer } from "react";
import Edit from "./Edit";
import axios from "axios";
import AddToBasket from "./AddToBasket";
import AddToWatchList from "./AddToWatchList";
import {reducer, init} from "./reducer";
import {
  BrowserRouter as Router,
  Switch,
  Route,
  Link,
  useRouteMatch,
  useParams,
} from "react-router-dom";
import { useFetch } from "./useFetch";

const Item = () => {


    const { id } = useParams();
    const [edit, setEdit] = useState(false);
    const [oneItem, dispatch] = useFetch(id)
    const [dataLoad, setdataLoad] = useState(false);


    if (!dataLoad) {
       setdataLoad(true)

    }
    console.log(oneItem)


  const editHandle = (e) => {
    return (
      <>
        <Edit
          key={oneItem._id}
          card={oneItem}
          setEdit={setEdit}
          data={oneItem}

        />
      </>
    );
  };

    return (

    <div className="container">
      <div className="row">
        <h1>Item </h1>
        {edit ? (
    editHandle()
  ) : (
                    <div className="col-md-4 col-sm-6">
                        <div
                            className="card"
                            style={{ borderColor: oneItem.website ? "" : "red" }}
                        >
                            <div className="card-header">
                                {oneItem.img == undefined
                                    ? ""
                                    : oneItem.img.map((e) => (
                                        <img
                                            style={{ maxWidth: "100%" }}
                                            src={"http://localhost:3001/" + e}
                                            alt={oneItem.name}
                                        />
                                    ))}
                            </div>
                            <div className="card-body">
                                <h2>{oneItem.title}</h2>
                                <h5>{oneItem.category}</h5>
                                <div>{oneItem.price}</div>
                                <div></div>
                                <div></div>
                            </div>
                            <button
                                onClick={() => setEdit(true)}
                                className="btn btn-secondary"
                            >
                                {" "}
                                update
                            </button>

                            <AddToBasket key={oneItem._id} card={oneItem} />
                            <AddToWatchList
                                key={oneItem._id}
                                data={oneItem}
                                dispatch={dispatch}
                            />
                        </div>
                    </div>
  )}
      </div>
    </div>
  );
};

export default Item;
