import Tilt from "react-parallax-tilt";

function Card3D({ img }) {
  console.log("img",img)
  return (
    <Tilt className="track3D"  glareEnable={true} glareMaxOpacity={0.8} glareColor="#ffffff" glarePosition="bottom" glareBorderRadius="20px">

      <div class="card3D" style={{ "backgroundImage": `url(${"http://localhost:3001/" + img})` }}>
      <div className="shadow3d"/>
              <div class="thumb" >
              <h2>Virtual Reality</h2>
        <span>Dreaming with your eyes open!</span>
        </div>
        </div>

    </Tilt>
  );
}

export default Card3D;
