import React, { useContext } from "react";
import { Link } from "react-router-dom";
import { prodactsConext } from "../context/contextData";

const NavBar = () => {
  const { data, dispatch, user, dispatchUser } = useContext(prodactsConext);
  return (
    <>



    <header class="header">
		<h1 class="logo"><a href="#">Online Shop</a></h1>
      <ul class="main-nav">
          <li><Link to="/sellerForm">Sell Somthing</Link></li>
          <li><Link to="/watchList">Watched List</Link></li>
          <li><Link to="/basketList">Basket List</Link></li>
          <li><Link to="/register">Register</Link></li>
          <li>






             {user.user ? (
          <a
            style={{ cursor: "pointer" }}
            onClick={() => {
              dispatchUser({ type: "LOGOUT_USER", payload: '' })
              let keysToRemove = ["user", "x-auth"];
              for (let key of keysToRemove) {
                localStorage.removeItem(key);
              }
            }
          }
          >
            logout
          </a>
        ) : (
          <Link to="/login">Login</Link>
            )}</li>
          <li>


        {user.user ? (
          <a className="text-primary">
            Hi!.. {user.user.firstName} {user.user.lastName}
          </a>
        ) : (
          ""
        )}
         </li> 

      </ul>







	</header> 

</>

  )
};

export default NavBar;
