import { useReducer, useEffect } from "react";
import { reducer } from "./reducer";
import axios from "axios";
import { API_ROOT } from "./service-info";



export const putReq = (body, id) => {
    return axios.put(`${API_ROOT}/${id}`, body).then((result) => result.data);
};

export const postReq = (body) => {
    return axios.post(`${API_ROOT}/`, body).then((result) => result.data);
};

export const getReq = (id) => {
 return axios.get(`${API_ROOT}/${id}`).then(result => result.data);
};
