import { watchReducer } from "../reducers/reducer";
import WatchList from "./WatchList";
import { getsReq } from "../api/rest-helper";

const WatchApi = () => {
  const [oneItem, dispatch] = useReducer(watchReducer, []);

  const { basket, dispatch1 ,user} = useContext(prodactsConext)




  return (
    <div><h1 className="text-center">Basket</h1>
       { basket.items.length ?  <div className="container">
        <div className="row">

          {basket.items.map((el) => {
            return <WatchList key={el._id} oneItem={el} dispatch1={dispatch1} basket={basket} user={user} />;
          })}
        </div>
      <div className='text-danger'>Total Price: {basket.total}</div>
        <Buy card={basket.items} />
      </div> : <h3 className="text-center">your Basket is Empty</h3>}
    </div>

  );
};

export default WatchApi;
