import { React } from "react";
import { Link } from "react-router-dom";

const Cards = ({ card, dispatch }) => {
  return (
    <>
      <div key={card._id} className="col-md-2 col-sm-8">
        <div className="card cardV">
          <Link to={`item/${card._id}`} onClick={()=> dispatch({type:"RECENT_ITEM", payload: card})}>
            <div className="card-header1">
              {card.img
                .map((e) => (
                  <img
                    key={card._id}
                    style={{ maxWidth: "100%" }}
                    src={"http://localhost:3001/" + e}
                    alt={card.name}
                  />
                ))
                .slice(0, 1)}
            </div>
            <div className="card-body">
              <h6>{card.title}</h6>
              <div>{card.category}</div>
              <span>{card.price} â‚¬</span>
            </div>
          </Link>
        </div>
      </div>
    </>
  );
};

export default Cards;
