import { React, useState, useEffect, useReducer,useContext } from "react";
import { itemReducer } from "../reducers/reducer";
import Edit from "./Edit";
import Buy from "../basket/Buy";
import { putReq, getReq } from "../api/rest-helper";
import { useParams } from "react-router-dom";
import { prodactsConext } from '../context/contextData'
import Login from '../users/Login'


const Item = () => {
  const { id } = useParams();
  const [edit, setEdit] = useState(false);

  const { dispatch1,user,dispatchOneItem,oneItem,dispatchWatch , watchItem} = useContext(prodactsConext)


  useEffect(() => {
      getReq(id).then((result) => {
        dispatchOneItem({ type: "FATCH_SUCCESS", payload:{product: result.data, userId:user.user._id} });
    });
  }, [id, edit]);




console.log('item', oneItem)

  return (
    <div className="container">
      <div className="row">

        {edit ? (
          !user.loggedIn ? <Login/> : <Edit key={oneItem._id} setEdit={setEdit} dispatchOneItem={dispatchOneItem} />
        ) : (

            <div className="col-md-4 col-sm-6">
              <h1>Item </h1>
            <div
              className="card"
              style={{ borderColor: oneItem.website ? "" : "red" }}
            >
              <div className="card-header">
                {oneItem.img == undefined
                  ? ""
                  : oneItem.img.map((e) => (
                      <img
                        style={{ maxWidth: "100%" }}
                        src={"http://localhost:3001/" + e}
                        alt={oneItem.name}
                      />
                    ))}
              </div>
              <div className="card-body">
                <h2>{oneItem.product.title}</h2>
                <h5>{oneItem.product.category}</h5>
                  <div>{oneItem.product.price}</div>
                  <div>{oneItem.quantity}</div>
              </div>
                {user.loggedIn && user.user && oneItem.owner && oneItem.owner._id == user.user._id ?  <button
                onClick={() => setEdit(!edit)}
                className="btn btn-secondary"
              >
                {" "}
                update
              </button>
               : <Buy key={oneItem._id} card={[oneItem]} />}
              <button
                className="btn"
                onClick={() =>
                  dispatchWatch({ type: "WATCH_ITEM", payload: oneItem })
                }
              >{watchItem.some(el=> el._id === oneItem._id) ? 'Watched' : 'add watch' }
                </button>
                <button
                className="btn"
                onClick={() =>
                  dispatch1({ type: "ADD_ITEM", payload: oneItem, })
                }
              >
                add to cart
                </button>
                <button onClick={()=> dispatchOneItem({type: 'DECROMENT'})}> - </button>
                <button onClick={() => dispatchOneItem({ type: 'INCROMENT'})}> + </button>

            </div>
          </div>

        )}
      </div>
    </div>
  );
};

export default Item;
