import React, { useState, useEffect } from "react";
import axios from "axios";
import {
  Route,
  Link
} from "react-router-dom";

const SellerForm = () => {
  const [inputText, setInputText] = useState({img:[]});



  const handleImage = (e) => {
    [...e.target.files].forEach((el) => {
      inputText.img.push(URL.createObjectURL(el))
    })
  };

  console.log('inputText', inputText);

  const handleChange = (e) => {
    setInputText({
      ...inputText,
      [e.target.name]: e.target.name == 'img' ? e.target.files[0] : e.target.value
    });
  };

  const handleSubmit = (e) => {
    e.preventDefault();

   const onData = new FormData();
    for(let key in inputText){
      if (key == 'img') {
        inputText[key].forEach(fileImg => {
         console.log('image',fileImg )
           if (typeof inputText[key] == 'object') onData.append(key, fileImg, fileImg.name)
        })


      } else {
        onData.append(key, inputText[key])
      }
    }
    console.log('ondata',onData)
    axios.post(`http://localhost:3001/products/`, onData )
    .then(result => {
        console.log('result',result.data)
    })
  };



  return (
    <div className='container'>
      <Link to="/">Home</Link>
      <form id="loginForm" method="POST" action="/#" >
        <legend>
          <fieldset>Seller Form</fieldset>
          <div className="formBox">
            <label for="name">Title</label>
					  <input
						 value={inputText.title} 
              className="email"
              type="text"
              autocomplete="off"
              name="title"
              onChange={handleChange}
            />
          </div>
          <div className="formBox">
            <label for="position">Type</label>
					  <input
						  value={inputText.type}
              class="pass"
              type="text"
              autocomplete="off"
              name="type"
              onChange={handleChange}
            />
          </div>
          <div class="formBox">
            <div className="formBox">
              <label for="price">Price</label>
						  <input
							  value={inputText.price}
                class="pass"
                type="text"
                autocomplete="off"
                name="price"
                onChange={handleChange}
              />
              <input type="file" multiple name="img" onChange={handleImage} />

            </div>
            <div class="formBox"></div>
            <button onClick={handleSubmit}>Submit</button>
          </div>
        </legend>
		  </form>
    </div>
  );
};

export default SellerForm;
